import"./style.css";import api from"./api.js";const displayMovies=async()=>{const n=document.getElementById("list-of-shows");n.innerHTML="";try{const s=await fetch(api.moviesApi),i=await s.json();if(!s.ok)return void(n.innerHTML="Server Down");i.forEach((s=>{n.innerHTML+=`\n        <section class="show_items">\n          <div class="">\n            <span class="" id=${s.id}>\n            </span>\n          </div>\n          <div class="show_image">\n            <img class="movie_image" src="${s.image.medium}" alt="${s.name}">\n          </div>\n          <div class="flex actions_name">\n            <p class="movie">${s.name}</p>\n            <div>\n              <i class="fa-solid fa-heart"></i>\n              <span>\n                <p>2 likes</p>\n              </span>\n            </div>\n          </div>\n          <div class="column flex">\n            <button class="button">Comments</button>\n            <button class="button">Reservations</button>\n          </div>\n        </section>`}))}catch(s){n.innerHTML="Error"}};displayMovies();